document.addEventListener("DOMContentLoaded",(function(){let e=document.querySelectorAll(".list-item"),t=document.querySelectorAll(".description-item");e.forEach((e,i)=>{e.addEventListener("click",()=>{t.forEach((e,t)=>{e.classList.remove("dsp-f")}),t[i].classList.add("dsp-f")})});let i=document.querySelector(".review-next"),n=document.querySelector(".review-prev");i.addEventListener("click",()=>{swiper.slideNext()}),n.addEventListener("click",()=>{swiper.slidePrev()})}));const swiper=new Swiper(".mySwiper",{pagination:{el:".swiper-pagination",type:"fraction"},width:580,navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}});